DROP DATABASE IF EXISTS Tours_and_AirTickets;
CREATE DATABASE Tours_and_AirTickets;
USE Tours_and_AirTickets;
CREATE TABLE roles 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(50) NOT NULL UNIQUE
);
CREATE TABLE users
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    role_id INT NOT NULL,
    name VARCHAR(100) NOT NULL,
    last_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) NOT NULL UNIQUE,
    password VARCHAR(255) NOT NULL,
    phone VARCHAR(20),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    photo VARCHAR(10000),
    FOREIGN KEY (role_id) REFERENCES roles(id) ON DELETE RESTRICT ON UPDATE CASCADE
);
CREATE TABLE tours 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR(150) NOT NULL,
    description TEXT,
    country VARCHAR(100),
    city VARCHAR(100),
    start_date DATE,
    end_date DATE,
    price DECIMAL(10,2),
    available BOOLEAN DEFAULT TRUE,
    transportation_included BOOLEAN DEFAULT FALSE,
    available_cities JSON NULL
);
CREATE TABLE tour_images (
    id INT AUTO_INCREMENT PRIMARY KEY,
    tour_id INT NOT NULL,
    image_url VARCHAR(1000) NOT NULL,
    sort_order INT DEFAULT 0,         
    FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE CASCADE
);
CREATE TABLE flights
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    airline VARCHAR(100),
    flight_number VARCHAR(20),
    departure_city VARCHAR(100),
    arrival_city VARCHAR(100),
    departure_time DATETIME,
    arrival_time DATETIME,
    price DECIMAL(10,2),
    available BOOLEAN DEFAULT TRUE,
    aircraft_type VARCHAR(50),
    total_seats INT,
    baggage_price DECIMAL(10,2) DEFAULT 15
);
CREATE TABLE flight_images (
    id INT AUTO_INCREMENT PRIMARY KEY,
    flight_id INT NOT NULL UNIQUE,   
    image_url VARCHAR(1000) NOT NULL,
    FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE CASCADE
);
CREATE TABLE flight_seats (
    id INT AUTO_INCREMENT PRIMARY KEY,
    flight_id INT NOT NULL,
    seat_number VARCHAR(10) NOT NULL,
    is_occupied BOOLEAN DEFAULT FALSE,
    occupied_until DATETIME NULL,
    FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE CASCADE,
    UNIQUE KEY unique_flight_seat (flight_id, seat_number)
);
CREATE TABLE bookings 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    tour_id INT NULL,
    flight_id INT NULL,
    booking_date DATETIME DEFAULT CURRENT_TIMESTAMP,
    status ENUM('Активно', 'Отменено', 'Завершено') DEFAULT 'Активно',
    travelers_count INT DEFAULT 1,
    transportation_type ENUM('self', 'company') DEFAULT 'self',
    departure_city VARCHAR(100) NULL,
    total_price DECIMAL(10,2) NOT NULL,
    selected_seats JSON NULL,
    has_baggage BOOLEAN DEFAULT FALSE,
    baggage_count INT DEFAULT 0,
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
    FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE SET NULL,
    FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE SET NULL
);
CREATE TABLE booking_history 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    booking_id INT NOT NULL,
    status ENUM('Активно', 'Отменено', 'Завершено'),
    changed_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (booking_id) REFERENCES bookings(id) ON DELETE CASCADE
);
CREATE TABLE favorites 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    tour_id INT NULL,
    flight_id INT NULL,
    added_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
    FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE SET NULL,
    FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE SET NULL
);
CREATE TABLE reviews
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    tour_id INT NULL,
    flight_id INT NULL,
    rating TINYINT CHECK (rating BETWEEN 1 AND 5),
    comment TEXT,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
    FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE SET NULL,
    FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE SET NULL
);
CREATE TABLE personalized_offers 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    tour_id INT NULL,
    flight_id INT NULL,
    discount_percent DECIMAL(5,2),
    valid_until DATE,
    description TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
    FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE SET NULL,
    FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE SET NULL
);
CREATE TABLE discounts 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR(150) NOT NULL,
    description TEXT,
    discount_percent DECIMAL(5,2),
    start_date DATE,
    end_date DATE,
    tour_id INT NULL,
    flight_id INT NULL,
    airline VARCHAR(100) NULL,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    is_active BOOLEAN DEFAULT TRUE,
    FOREIGN KEY (tour_id) REFERENCES tours(id) ON DELETE SET NULL,
    FOREIGN KEY (flight_id) REFERENCES flights(id) ON DELETE SET NULL
);
CREATE TABLE feedback 
(
    id INT AUTO_INCREMENT PRIMARY KEY,
    user_id INT NOT NULL,
    manager_id INT NULL,
    message TEXT NOT NULL,
    response TEXT,
    status ENUM('open', 'answered', 'closed') DEFAULT 'open',
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    updated_at DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
    FOREIGN KEY (manager_id) REFERENCES users(id) ON DELETE SET NULL
);
CREATE TABLE chat_messages (
    id INT AUTO_INCREMENT PRIMARY KEY,
    feedback_id INT NOT NULL,
    user_id INT NOT NULL,
    manager_id INT NULL,
    message_text TEXT NOT NULL,
    is_from_manager BOOLEAN DEFAULT FALSE,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY (feedback_id) REFERENCES feedback(id) ON DELETE CASCADE,
    FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE,
    FOREIGN KEY (manager_id) REFERENCES users(id) ON DELETE SET NULL
);
CREATE TABLE password_reset_codes (
    id INT AUTO_INCREMENT PRIMARY KEY,
    email VARCHAR(100) NOT NULL,
    code VARCHAR(6) NOT NULL,
    expires_at TIMESTAMP NOT NULL,
    used BOOLEAN DEFAULT FALSE,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    INDEX idx_email_code (email, code),
    INDEX idx_expires (expires_at)
);
